select

from scan_task
where 1=1

	and TASK_NAME=#{name, jdbcType=VARCHAR}


	and TASK_GROUP=#{group, jdbcType=VARCHAR}


	and TASK_TYPE=#{type, jdbcType=VARCHAR}


	and TASK_STATE=#{state, jdbcType=VARCHAR}

